INSERT INTO tb_project (user_id, created_date, title, description, image) VALUES (1,   now(),  'Agrobairros',                         'Os projetos urbanos que combinam casas e áreas de produção de alimentos solucionam duas demandas de quem vive em cidades: um contato maior com a natureza e a proximidade com a produção de alimentos – o que gera menos impacto ambiental e, muitas vezes, acesso à comida produzida de maneira mais saudável e sustentável. Nesta publicação, veja mais sobre quais conteúdos didáticos, eventos e atividades que estão relacionadas aos Agrobairros em Caxias', 'https://www.graphikmaquetas.com.ar/val8.jpg');
INSERT INTO tb_project (user_id, created_date, title, description, image) VALUES (2,   now(),  'Coleta e Filtragem de Água',          'Os projetos de coleta e filtragem de água visão prover a criação de estruturas que possam sar facilmente instaladas em diversos locais da cidade permitindo também que a água da chuva seja coletada, filtrada e armazenada para os períodos mais secos, auxiliando na gestão sustentável da água. Neste projeto, veja mais sobre quais conteúdos didáticos, eventos e atividades que estão relacionadas a Coleta e Filtragem de Água em Caxias', 'https://img.freepik.com/premium-vector/water-drop-shape-blue-water-drop-logo-design-water-oil-drop_275352-810.jpg');
