INSERT INTO tb_project (user_id, created_date, title, description, image) VALUES (3,   now(),  'Arquitetura Acessível e Sutentável',  'Os projetos de moradia que envolvem uma arquitetura de preço acessível, uma alta velocidade na produção e um baixo impacto no meito ambiente podem ser uma solução prática para o problema de pessoas em situação de vunerabilidade com moradia. Neste projeto, veja mais sobre quais conteúdos didáticos, eventos e atividades que estão relacionadas a Arquitetura Acessível e Sustentável em Caxias', 'https://www.aubicon.com.br/wp-content/uploads/2021/03/04_arq_sustentavel_blog_15.jpg');
