INSERT INTO tb_event (project_id, user_id, event_date, created_date, event_status, image, title, description) VALUES (1, 1,   '2025-01-03',   now(),   'PLANEJANDO',   'https://cdn1.iconfinder.com/data/icons/business-vivid-vol-2/256/Seminar-1024.png',  'Seminário sobre a distribuição de alimentos nos Agrobairros em Caxias', 'Este evento trata-se de um seminário sobre a distribuição de alimentos nos Agrobairros em Caxias. O Evento reunirá especialistas, agricultores locais, residentes nos bairros envolvidos e interessados na temática para discutir as práticas e políticas que possam garantir uma entrega eficiente e justa de alimentos nas áreas dos Agrobairros Caxienses. Será uma oportunidade para aprender, trocar experiências e buscar soluções inovadoras para melhorar a cadeia de distribuição, impactando positivamente a segurança alimentar e a qualidade de vida da comunidade.');
INSERT INTO tb_event (project_id, user_id, event_date, created_date, event_status, image, title, description) VALUES (1, 1,   '2025-01-09',   now(),   'PLANEJANDO',   'https://cdn2.iconfinder.com/data/icons/career-advancement-4/512/Practice-1024.png',  'Atividade prática para estabelecimento de hortas comunitárias no bairro Nova Caxias', 'O evento é uma iniciativa que visa transformar espaços urbanos subutilizados em hortas comunitárias produtivas no bairro Nova Caxias. Este evento prático reunirá moradores do bairro para colaborar na criação de hortas que não só fornecerão alimentos saudáveis, mas também promoverão a integração social, a educação ambiental e a sustentabilidade.');
INSERT INTO tb_event (project_id, user_id, event_date, created_date, event_status, image, title, description) VALUES (1, 2,   '2025-02-15',   now(),   'PLANEJANDO',   'https://cdn2.iconfinder.com/data/icons/system-filled-outline/64/warning-danger-system-icon-512.png',     'Palestra sobre riscos e cuidados com pragas nas hortas em Agrobairros de Caxias', 'Este evento é uma iniciativa educativa voltada para os moradores de Agrobairros que desejam aprender mais sobre como proteger suas hortas das pragas mais comuns. Este evento visa fornecer conhecimentos práticos e teóricos sobre a identificação, prevenção e controle de pragas, promovendo a conscientização de uma agricultura urbana mais saudável e sustentável.');
INSERT INTO tb_event (project_id, user_id, event_date, created_date, event_status, image, title, description) VALUES (1, 2,   '2025-03-11',   now(),   'PLANEJANDO',   'https://cdn-icons-png.flaticon.com/512/9912/9912471.png',  'Apresentação e debate dos dados periódicos sobre as colheitas nos Agrobairros em Caxias', 'O evento é uma oportunidade para os moradores, gestores públicos e interessados se reunirem para analisar e discutir os dados mais recentes sobre o desenvolvimento das colheitas dos Agrobairros na cidade. Este evento visa promover a transparência, a participação comunitária e a tomada de decisões informadas com base em dados concretos.');
INSERT INTO tb_event (project_id, user_id, event_date, created_date, event_status, image, title, description) VALUES (1, 3,   '2025-04-18',   now(),   'PROGRAMADO',   'https://cdn0.iconfinder.com/data/icons/smart-technology-malibu-volume-1/128/intelligent-power-512.png',  'Criação de soluções para automatizar a produção de alimentos nos Agrobairros em Caxias', 'O evento objetiva reunir especialistas, agricultores urbanos e entusiastas da tecnologia para discutir e desenvolver soluções inovadoras que automatizem a produção de alimentos nos Agrobairros de Caxias. Este evento busca promover a adoção de tecnologias avançadas para aumentar a eficiência, sustentabilidade e produtividade das hortas urbanas.');
INSERT INTO tb_event (project_id, user_id, event_date, created_date, event_status, image, title, description) VALUES (1, 3,   '2024-10-12',   now(),   'PROGRAMADO',   'https://cdn-icons-png.flaticon.com/512/4730/4730760.png',  'Videoconfêrencia sobre plantio de cereais na Vila Conquista de Caxias', 'Este evento promove o compartilhamento de conhecimentos e práticas sobre o cultivo de cereais em ambientes urbanos. Este evento online reunirá especialistas, agricultores urbanos e interessados para discutir técnicas, desafios e inovações no plantio de cereais, promovendo a sustentabilidade e a segurança alimentar na Vila Conquista');
INSERT INTO tb_event (project_id, user_id, event_date, created_date, event_status, image, title, description) VALUES (1, 4,   '2024-10-20',   now(),   'PROGRAMADO',   'https://cdn0.iconfinder.com/data/icons/business-situations-flat-1/64/annual-report-graph-summary-company-1024.png',  'Produção de relatórios sobre benefícios dos Agrobairros na economia de Caxias', 'O evento que conta com a participação de especialistas, gestores públicos, economistas e membros da comunidade busca analisar e documentar atráves da produção de relatórios os impactos econômicos positivos dos Agrobairros na cidade de Caxias. Este evento busca promover a compreensão e a valorização da agricultura urbana como um motor de desenvolvimento econômico.');
INSERT INTO tb_event (project_id, user_id, event_date, created_date, event_status, image, title, description) VALUES (1, 4,   '2024-11-03',   now(),   'PROGRAMADO',   'https://cdn1.iconfinder.com/data/icons/business-vivid-vol-2/256/Seminar-1024.png',  'Seminário sobre a conservação de alimentos nos Agrobairros em Caxias', 'Este evento é um seminário sobre a Conservação de Alimentos em Agrobairros é uma iniciativa educativa que objetiva capacitar os moradores e agricultores urbanos sobre as melhores práticas e tecnologias para a conservação de alimentos. Este evento reunirá especialistas, técnicos e a comunidade para discutir métodos eficazes de preservação, garantindo a qualidade e a segurança dos alimentos produzidos nos Agrobairros.');
INSERT INTO tb_event (project_id, user_id, event_date, created_date, event_status, image, title, description) VALUES (1, 4,   '2024-11-09',   now(),   'ADIADO',       'https://cdn2.iconfinder.com/data/icons/career-advancement-4/512/Practice-1024.png',  'Atividade prática para estabelecimento de hortas comunitárias no bairro Trizidela', 'O evento é uma iniciativa que visa transformar espaços urbanos subutilizados em hortas comunitárias produtivas no bairro Trizidela. Este evento prático reunirá moradores do bairro para colaborar na criação de hortas que não só fornecerão alimentos saudáveis, mas também promoverão a integração social, a educação ambiental e a sustentabilidade.');
